mapper.select:
select
UserId, Password, NickName,AuthorId from UserInfo
where UserId = #{userId}

mapper.update:
update UserInfo set NickName = #{nickName},
Email=#{email} where UserId =
#{userId}

update InstructorInfo set
IntroContent = #{introContent}, History = #{history} where UserId =
#{userId}

mapper.select:
select a.UserId, a.ReservationDate, a.RoomId, b.RoomName, a.PayAmount,
a.PaymentDate, a.ReservationTime from ReservationInfo a join RoomInfo
b on a.RoomId = b.id where UserId =#{userId}
