mapper.update:
update UserInfo set MessageCount = (
select count(*) from MessageInfo where Recipient = #{recipient} and
ReadStatus = '읽지않음' and ShowStatus='정상' ) where UserId = #{recipient};

mapper.select:
select * from MessageInfo where Recipient =#{recipient} and ShowStatus = '정상'
and (MessageContent like CONCAT('%',#{cri.keyword},'%') or Sender like CONCAT('%',#{cri.keyword},'%') or SenderNickName like CONCAT('%',#{cri.keyword},'%') )
order by SendDate DESC limit #{cri.pageStart}, #{cri.perPageNum}

select * from
MessageInfo where Sender = #{sender} 

and (MessageContent like CONCAT('%',#{cri.keyword},'%') or Recipient like CONCAT('%',#{cri.keyword},'%') or RecipientNickName like CONCAT('%',#{cri.keyword},'%') )

order by SendDate DESC
limit #{cri.pageStart}, #{cri.perPageNum}
